st.title("‚öñÔ∏è ‡∏£‡∏∞‡∏ö‡∏ö‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏£‡∏π‡πâ‡∏ï‡∏±‡∏ß‡∏ö‡∏ó‡∏Å‡∏é‡∏´‡∏°‡∏≤‡∏¢‡πÄ‡∏ä‡∏¥‡∏á‡∏•‡∏∂‡∏Å (Security Law)")
st.write("‡∏ï‡∏±‡∏ß‡∏ö‡∏ó‡πÄ‡∏ï‡πá‡∏°‡∏Ñ‡∏£‡∏ö‡∏ó‡∏∏‡∏Å‡∏ß‡∏£‡∏£‡∏Ñ ‡∏û‡∏£‡πâ‡∏≠‡∏°‡πÑ‡∏Æ‡πÑ‡∏•‡∏ó‡πå‡πÅ‡∏¢‡∏Å‡∏≠‡∏á‡∏Ñ‡πå‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö‡∏°‡∏≤‡∏ï‡∏£‡∏≤")

tab1, tab2, tab3 = st.tabs(["üîµ ‡∏Ñ‡πâ‡∏≥‡∏õ‡∏£‡∏∞‡∏Å‡∏±‡∏ô", "üü¢ ‡∏à‡∏≥‡∏ô‡∏≠‡∏á", "üü† ‡∏à‡∏≥‡∏ô‡∏≥"])

def render_law_page(category):
    col_nav, col_main = st.columns([1, 2.5])
    with col_nav:
        st.subheader("‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏°‡∏≤‡∏ï‡∏£‡∏≤")
        # ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏≤‡∏ï‡∏£‡∏≤‡∏ó‡∏µ‡πà‡∏°‡∏µ‡πÉ‡∏ô db
        available_secs = list(law_db[category].keys())
        selected = st.radio(f"‡∏°‡∏≤‡∏ï‡∏£‡∏≤‡πÉ‡∏ô‡∏´‡∏°‡∏ß‡∏î {category}", available_secs, key=f"r_{category}")
    
    with col_main:
        content = law_db[category][selected]
        st.subheader(f"‡∏°‡∏≤‡∏ï‡∏£‡∏≤ {selected}")
        
        # ‡πÅ‡∏™‡∏î‡∏á Legend
        st.markdown("<small>üîµ ‡∏ö‡∏∏‡∏Ñ‡∏Ñ‡∏• | üî¥ ‡∏Å‡∏≤‡∏£‡∏Å‡∏£‡∏∞‡∏ó‡∏≥/‡πÄ‡∏á‡∏∑‡πà‡∏≠‡∏ô‡πÑ‡∏Ç | üü¢ ‡∏ß‡∏±‡∏ï‡∏ñ‡∏∏/‡∏ó‡∏£‡∏±‡∏û‡∏¢‡πå</small>", unsafe_allow_html=True)
        
        # ‡∏õ‡∏£‡∏∞‡∏°‡∏ß‡∏•‡∏ú‡∏•‡πÑ‡∏Æ‡πÑ‡∏•‡∏ó‡πå
        highlighted = highlight_law(content["text"])
        st.markdown(f"<div class='law-text-area'>{highlighted}</div>", unsafe_allow_html=True)
        
        # ‡πÅ‡∏™‡∏î‡∏á‡∏é‡∏µ‡∏Å‡∏≤
        st.markdown(f"<div class='dika-section'><b>üèõÔ∏è ‡∏é‡∏µ‡∏Å‡∏≤‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö:</b> {content['dika']}</div>", unsafe_allow_html=True)

with tab1: render_law_page("‡∏Ñ‡πâ‡∏≥‡∏õ‡∏£‡∏∞‡∏Å‡∏±‡∏ô")
with tab2: render_law_page("‡∏à‡∏≥‡∏ô‡∏≠‡∏á")
with tab3: render_law_page("‡∏à‡∏≥‡∏ô‡∏≥")
